import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

export default function Estimate() {
  const navigate = useNavigate();
  const location = useLocation();

  // Expect state: { deviceCounts: { [roomId]: { [deviceId]: count } }, rooms: [...], stubDevices: [...] }
  const { deviceCounts = {}, rooms = [], stubDevices = [] } = location.state || {};

  // Define savings per device per month
  const savingsRates = {
    'LED Bulb': 2,             // $2 per bulb
    'Smart Thermostat': 8,     // $8 per thermostat
    'Energy Saver Fan': 3      // $3 per fan
  };

  // Calculate total monthly savings across all rooms
  let totalMonthly = 0;
  rooms.forEach(room => {
    const counts = deviceCounts[room.id] || {};
    stubDevices.forEach(device => {
      const qty = counts[device.id] || 0;
      const rate = savingsRates[device.name] || 0;
      totalMonthly += rate * qty;
    });
  });

  const totalYearly = totalMonthly * 12;

  return (
    <div className="table" style={{ textAlign: 'center' }}>
      <p>Estimated Savings:</p>
      <p style={{ fontSize: '2rem', margin: '1rem 0' }}>${totalMonthly.toFixed(2)} <small>monthly</small></p>
      <p style={{ fontSize: '2rem', margin: '1rem 0' }}>${totalYearly.toFixed(2)} <small>yearly</small></p>
      <button onClick={() => navigate('/appointment')} style={{ padding: '0.75rem 1.5rem', fontSize: '1rem', borderRadius: '0.5rem', cursor: 'pointer' }}>
        Book an appointment
      </button>
    </div>
  );
}
