import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

export default function RoomsPage() {
  const [rooms, setRooms] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [selectedType, setSelectedType] = useState('');
  const [expanded, setExpanded] = useState({});
  const [deviceCounts, setDeviceCounts] = useState({});
  const navigate = useNavigate();

  // Stub devices until API is ready
  const stubDevices = [
    { id: 'dev1', name: 'LED Bulb' },
    { id: 'dev2', name: 'Smart Thermostat' },
    { id: 'dev3', name: 'Energy Saver Fan' }
  ];

  const roomTypes = [
    { value: 'BEDROOM', label: 'Bedroom' },
    { value: 'KITCHEN', label: 'Kitchen' },
    { value: 'DINING_ROOM', label: 'Dining Room' },
    { value: 'BATHROOM', label: 'Bathroom' },
    { value: 'LIVING_ROOM', label: 'Living Room' },
    { value: 'OFFICE', label: 'Office' },
  ];

  useEffect(() => {
    async function fetchRooms() {
      try {
        const res = await fetch('/rooms');
        const data = await res.json();
        setRooms(data.map(r => ({ ...r, devices: r.devices || [] })));
      } catch (e) {
        console.error('Failed to load rooms', e);
      }
    }
    fetchRooms();
  }, []);

  const buttonStyle = { padding: '0.5rem 1rem', fontSize: '1rem', borderRadius: '0.5rem', cursor: 'pointer' };
  const smallButton = { padding: '0.25rem 0.5rem', fontSize: '0.875rem', borderRadius: '0.5rem', cursor: 'pointer' };

  const handleAddClick = () => setShowForm(true);
  const handleCancel = () => { setShowForm(false); setSelectedType(''); };

  const handleCreate = async () => {
    if (!selectedType) return;
    try {
      const res = await fetch('/rooms', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ type: selectedType }) });
      const newRoom = await res.json(); newRoom.devices = [];
      setRooms(prev => [newRoom, ...prev]); handleCancel();
    } catch { alert('Failed to create room'); }
  };

  const handleToggle = roomId => setExpanded(prev => ({ ...prev, [roomId]: !prev[roomId] }));

  const handleCountChange = (roomId, deviceId, value) => {
    setDeviceCounts(prev => ({
      ...prev,
      [roomId]: { ...prev[roomId], [deviceId]: value }
    }));
    setRooms(prev => prev.map(room => {
      if (room.id !== roomId) return room;
      return {
        ...room,
        devices: stubDevices.filter(d => prev[roomId]?.[d.id] > 0).map(d => ({ ...d, count: prev[roomId][d.id] }))
      };
    }));
  };

  return (
    <div className="table" style={{ textAlign: 'left', position: 'relative' }}>
      <h1>Your Rooms</h1>
      <button onClick={handleAddClick} style={buttonStyle}>+ New Room</button>

      {showForm && (
        <div style={{ position: 'fixed', inset: 0, backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }} onClick={handleCancel}>
          <div style={{ background: '#444', padding: '1.5rem', borderRadius: '0.5rem', minWidth: 300 }} onClick={e => e.stopPropagation()}>
            <h3 style={{ color: '#fff', marginBottom: '1rem' }}>New Room</h3>
            <select value={selectedType} onChange={e => setSelectedType(e.target.value)} style={{ width: '100%', padding: '0.5rem', fontSize: '1rem', borderRadius: '0.5rem', marginBottom: '1rem' }}>
              <option value="">-- select type --</option>
              {roomTypes.map(rt => <option key={rt.value} value={rt.value}>{rt.label}</option>)}
            </select>
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '0.5rem' }}>
              <button onClick={handleCancel} style={smallButton}>Cancel</button>
              <button onClick={handleCreate} style={buttonStyle}>Create</button>
            </div>
          </div>
        </div>
      )}

      <div className="devices-list">
        {rooms.map(room => (
          <div key={room.id} className="device-card" style={{ width: '30%', minWidth: 120, textAlign: 'left' }}>
            <h3 style={{ margin: '0 0 0.5rem' }}>{room.name}</h3>
            <p style={{ margin: '0.25rem 0' }}>Devices:</p>
            <button onClick={() => handleToggle(room.id)} style={smallButton}>{expanded[room.id] ? 'Hide' : 'Show'}</button>

            {expanded[room.id] && (
              <div style={{ marginTop: '0.5rem' }}>
                {stubDevices.map(device => (
                  <div key={device.id} style={{ display: 'flex', alignItems: 'center', marginBottom: '0.25rem' }}>
                    <span style={{ flex: 1 }}>{device.name}</span>
                    <input
                      type="number"
                      min={0}
                      placeholder="0"
                      value={deviceCounts[room.id]?.[device.id] || ''}
                      onChange={e => handleCountChange(room.id, device.id, Number(e.target.value))}
                      style={{ width: '3rem', padding: '0.25rem', fontSize: '0.875rem', borderRadius: '0.25rem' }}
                    />
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>

      <button onClick={() => navigate('/estimate')} style={{ ...buttonStyle, width: '100%', marginTop: '2rem', fontSize: '1.25rem' }}>Estimate</button>
    </div>
  );
}
