// src/pages/AdminPanel.jsx
import React, { useEffect, useState } from 'react';
import { jwtDecode } from 'jwt-decode';
import '../App.css';
import { useUserId } from '../hooks/useUserId';

/** util: read numeric userId from JWT or fallback to /users/me */


export default function AdminPanel() {
  const userId = useUserId();
  const token  = localStorage.getItem('accessToken');

  const [smart,       setSmart]       = useState([]);   // smart groups
  const [dumb,        setDumb]        = useState([]);   // all dumb devices
  const [linkSelect,  setLinkSelect]  = useState({});   // smartId -> dumbId
  const [showForm,    setShowForm]    = useState(false);
  const [newName,     setNewName]     = useState('');
  const [error,       setError]       = useState('');

  /* ───── Fetch initial data ───── */
  useEffect(() => {
    if (!userId) return;

    async function load() {
      try {
        const [smartRes, statsRes, devicesRes] = await Promise.all([
          fetch(`/api/users/${userId}/smart-devices`, {
            headers: { Authorization: `Bearer ${token}` }
          }),
          fetch('/api/usage-stats', {
            headers: { Authorization: `Bearer ${token}` }
          }),
          fetch(`/api/users/${userId}/devices`, { // real dumb devices WITH IDs
          headers: { Authorization: `Bearer ${token}` }
         })
        ]);
        if (smartRes.ok) setSmart(await smartRes.json());
        if (statsRes.ok && devicesRes.ok) {
            const stats    = await statsRes.json();    // has deviceName
            const devices  = await devicesRes.json();  // has id + name

            // merge by (case-insensitive) name
            const merged = stats.map(s => {
            const found = devices.find(
                d => d.name?.toLowerCase() === s.deviceName?.toLowerCase()
            );
            return found ? { ...s, id: found.id } : null; // keep only matched
            }).filter(Boolean);

            setDumb(merged);   // now every item has { id, deviceName, … }
        }
      } catch { /* ignore for brevity */ }
    }
    load();
  }, [userId, token]);

  /* ───── Derived: which dumb devices are still free ───── */
  const linkedIds = new Set(
    smart.flatMap(s => (s.devices || []).map(d => d.id))
  );
  const availableDumb = dumb.filter(d => !linkedIds.has(d.id));

  /* ───── Handlers ───── */
  const handleCreateSmart = async () => {
    if (!newName.trim()) return;
    try {
      const res = await fetch(`/api/users/${userId}/smart-devices`, {
        method : 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization : `Bearer ${token}`
        },
        body: JSON.stringify({ name: newName.trim() })
      });
      if (!res.ok) throw new Error('Failed to create');
      const created = await res.json();
      setSmart(prev => [...prev, created]);
      setShowForm(false);
      setNewName('');
    } catch (e) {
      setError(e.message);
    }
  };

  const handleLink = async (smartId) => {
    const deviceId = linkSelect[smartId];
    if (!deviceId) return;
    try {
      const res = await fetch(
        `/api/users/${userId}/smart-devices/${smartId}/devices`,
        {
          method : 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization : `Bearer ${token}`
          },
          body: JSON.stringify({ deviceId })
        }
      );
      if (!res.ok) throw new Error('Linking failed');
      // On success, move the dumb device inside that smart group locally
      const linkedDevice = availableDumb.find(d => d.id === deviceId);
      setSmart(prev =>
        prev.map(s =>
          s.id === smartId
            ? { ...s, devices: [...(s.devices || []), linkedDevice] }
            : s
        )
      );
      setLinkSelect(prev => ({ ...prev, [smartId]: '' }));
    } catch (e) {
      setError(e.message);
    }
  };

  /* ───── Render ───── */
  const button = { padding: '0.5rem 1rem', fontSize: '1rem',
                   borderRadius: '0.5rem', cursor: 'pointer' };

  return (
    <div className="table" style={{ textAlign: 'left' }}>
      <h1>Admin panel</h1>

      {/* error banner */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* smart-device cards */}
      <div className="devices-list" style={{ gap: '1rem' }}>
        {smart.map(s => (
          <div key={s.id} className="device-card" style={{ padding: '1rem' }}>
            <h3 style={{ margin: '0 0 0.5rem' }}>{s.name}</h3>
            <p style={{ margin: '0 0 0.5rem' }}>
              Linked dumb devices: {(s.devices || []).length}
            </p>

            {/* list linked */}
            {(s.devices || []).map(d => (
              <div key={d.id} style={{ fontSize: '0.9rem' }}>
                • {d.deviceName || d.name}
              </div>
            ))}

            {/* link selector */}
            <div style={{
              display: 'grid',
              gridTemplateColumns: '1fr auto',
              gap: '0.5rem',
              marginTop: '1rem'
            }}>
              <select
                value={linkSelect[s.id] || ''}
                onChange={e =>
                  setLinkSelect(prev => ({ ...prev, [s.id]: Number(e.target.value) }))
                }
              >
                <option key="placeholder" value="">
                 -- choose dumb device --
                </option>
                 {availableDumb.map((d, idx) => (
                <option key={`dumb-${d.id}-${idx}`} value={d.id}>
                    {d.deviceName || d.name}
                </option>
                 ))}
              </select>
              <button
                style={button}
                onClick={() => handleLink(s.id)}
                disabled={!linkSelect[s.id]}
              >
                Link
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* + button */}
      <button style={{ ...button, width: '100%', marginTop: '2rem',
                       fontSize: '1.25rem' }}
              onClick={() => setShowForm(true)}>
        + New smart device
      </button>

      {/* modal to create smart device */}
      {showForm && (
        <div style={{
          position: 'fixed', inset: 0,
          background: 'rgba(0,0,0,0.6)',
          display: 'flex', alignItems: 'center', justifyContent: 'center',
          zIndex: 1000
        }} onClick={() => setShowForm(false)}>
          <div style={{ background: '#444', padding: '1.5rem',
                        borderRadius: '0.5rem', minWidth: 300 }}
               onClick={e => e.stopPropagation()}>
            <h3 style={{ marginBottom: '1rem' }}>New smart device</h3>
            <input
              placeholder="Name"
              value={newName}
              onChange={e => setNewName(e.target.value)}
              style={{ width: '100%', padding: '0.5rem',
                       borderRadius: '0.25rem', marginBottom: '1rem' }}
            />
            <div style={{ display: 'flex', justifyContent: 'flex-end',
                          gap: '0.5rem' }}>
              <button style={button} onClick={() => setShowForm(false)}>
                Cancel
              </button>
              <button style={button} onClick={handleCreateSmart}>
                Create
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
