import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const BACKEND = process.env.REACT_APP_BACKEND_URL;
const specialChars = /[!@#*()\-]/;
const uppercaseRegex = /[A-Z]/;

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const isLengthValid = password.length >= 6;
  const hasUppercase = uppercaseRegex.test(password);
  const hasSpecialChar = specialChars.test(password);
  const isPasswordValid = isLengthValid && hasUppercase && hasSpecialChar;

  const handleLogin = async () => {
    if (!isPasswordValid) {
      setError('Password does not meet the requirements');
      return;
    }
    setError('');
    try {
      const response = await fetch(`${BACKEND}/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      if (!response.ok) throw new Error('Login error');
      const data = await response.json();
      localStorage.setItem('token', data.token);
      navigate('/appointment');
    } catch (e) {
      setError('Invalid email or password');
    }
  };

  return (
    <div className="table">
      <h2>Login</h2>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={e => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={e => setPassword(e.target.value)}
      />

      <ul style={{ listStyle: 'none', padding: 0, textAlign: 'left', maxWidth: 300, margin: '1rem auto' }}>
        <li style={{ color: isLengthValid ? 'lightgreen' : '#ff6b6b' }}>
          {isLengthValid ? '✔' : '✖'} Minimum 6 characters
        </li>
        <li style={{ color: hasUppercase ? 'lightgreen' : '#ff6b6b' }}>
          {hasUppercase ? '✔' : '✖'} At least one uppercase letter
        </li>
        <li style={{ color: hasSpecialChar ? 'lightgreen' : '#ff6b6b' }}>
          {hasSpecialChar ? '✔' : '✖'} At least one special character (! @ # * ( ) -)
        </li>
      </ul>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      <button onClick={handleLogin} disabled={!isPasswordValid}>
        Proceed
      </button>
    </div>
  );
}
