import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

export default function RoomsPage() {
  const [rooms, setRooms] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [selectedType, setSelectedType] = useState('');
  const [expanded, setExpanded] = useState({});
  const [newDeviceNames, setNewDeviceNames] = useState({});
  const navigate = useNavigate();

  const roomTypes = [
    { value: 'BEDROOM', label: 'Bedroom' },
    { value: 'KITCHEN', label: 'Kitchen' },
    { value: 'DINING_ROOM', label: 'Dining Room' },
    { value: 'BATHROOM', label: 'Bathroom' },
    { value: 'LIVING_ROOM', label: 'Living Room' },
    { value: 'OFFICE', label: 'Office' },
  ];

  const handleAddClick = () => setShowForm(true);
  const handleCreate = async () => {
    if (!selectedType) return;
    try {
      const res = await fetch('/rooms', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ type: selectedType })
      });
      const newRoom = await res.json();
      newRoom.devices = newRoom.devices || [];
      setRooms(r => [newRoom, ...r]);
      setShowForm(false);
      setSelectedType('');
    } catch {
      alert('Failed to create room');
    }
  };

  const handleToggle = id => setExpanded(e => ({ ...e, [id]: !e[id] }));
  const handleDeviceNameChange = (roomId, value) => {
    setNewDeviceNames(n => ({ ...n, [roomId]: value }));
  };
  const handleAddDevice = roomId => {
    const name = (newDeviceNames[roomId] || '').trim();
    if (!name) return;
    setRooms(r => r.map(room => room.id === roomId ? ({
      ...room,
      devices: [...(room.devices || []), { id: Date.now(), name }]
    }) : room));
    setNewDeviceNames(n => ({ ...n, [roomId]: '' }));
  };

  const buttonStyle = { padding: '0.5rem 1rem', fontSize: '1rem', borderRadius: '0.5rem', cursor: 'pointer' };
  const smallButtonStyle = { padding: '0.25rem 0.5rem', fontSize: '0.875rem', borderRadius: '0.5rem', cursor: 'pointer' };

  return (
    <div className="table" style={{ textAlign: 'left', position: 'relative' }}>
      <h1>Your Rooms</h1>
      <button onClick={handleAddClick} style={buttonStyle}>+ New Room</button>

      {/* Modal popup for room creation */}
      {showForm && (
        <div style={{
          position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex',
          alignItems: 'center', justifyContent: 'center', zIndex: 1000
        }} onClick={() => setShowForm(false)}>
          <div style={{ background: '#444', padding: '1.5rem', borderRadius: '0.5rem', minWidth: '300px' }} onClick={e => e.stopPropagation()}>
            <h3 style={{ color: '#fff', marginBottom: '1rem' }}>New Room</h3>
            <select
              value={selectedType}
              onChange={e => setSelectedType(e.target.value)}
              style={{ width: '100%', padding: '0.5rem', fontSize: '1rem', borderRadius: '0.5rem', marginBottom: '1rem' }}
            >
              <option value="">-- select type --</option>
              {roomTypes.map(rt => (
                <option key={rt.value} value={rt.value}>{rt.label}</option>
              ))}
            </select>
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '0.5rem' }}>
              <button onClick={() => setShowForm(false)} style={smallButtonStyle}>Cancel</button>
              <button onClick={handleCreate} style={buttonStyle}>Create</button>
            </div>
          </div>
        </div>
      )}

      <div className="devices-list">
        {rooms.map(room => (
          <div key={room.id} className="device-card" style={{ width: '30%', minWidth: '120px', textAlign: 'left' }}>
            <h3 style={{ margin: '0 0 0.5rem' }}>{room.name}</h3>
            <p style={{ margin: '0.25rem 0' }}>Devices: {(room.devices || []).length}</p>
            <button onClick={() => handleToggle(room.id)} style={smallButtonStyle}>
              {expanded[room.id] ? 'Hide Devices' : 'Show Devices'}
            </button>

            {expanded[room.id] && (
              <div style={{ marginTop: '0.75rem' }}>
                <ul style={{ paddingLeft: '1rem' }}>
                  {(room.devices || []).map(dev => (
                    <li key={dev.id} style={{ marginBottom: '0.25rem' }}>{dev.name}</li>
                  ))}
                </ul>
                <div style={{ display: 'flex', gap: '0.5rem', marginTop: '0.5rem' }}>
                  <input
                    type="text"
                    placeholder="Device name"
                    value={newDeviceNames[room.id] || ''}
                    onChange={e => handleDeviceNameChange(room.id, e.target.value)}
                    style={{ flex: 1, padding: '0.5rem', fontSize: '1rem', borderRadius: '0.5rem' }}
                  />
                  <button onClick={() => handleAddDevice(room.id)} style={buttonStyle}>Add</button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      <button
        onClick={() => navigate('/estimate')}
        style={{ ...buttonStyle, width: '100%', marginTop: '2rem', fontSize: '1.25rem' }}
      >Estimate</button>
    </div>
  );
}
