import React, { useState } from 'react';
import '../App.css';

export default function RoomsPage() {
  const [rooms, setRooms] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [selectedType, setSelectedType] = useState('');

  const roomTypes = [
    { value: 'BEDROOM', label: 'Спальня' },
    { value: 'KITCHEN', label: 'Кухня' },
    { value: 'DINING_ROOM', label: 'Столовая' },
    { value: 'BATHROOM', label: 'Ванная' },
    { value: 'LIVING_ROOM', label: 'Гостиная' },
    { value: 'OFFICE', label: 'Офис' },
  ];

  const handleAddClick = () => {
    setShowForm(true);
  };

  const handleCreate = async () => {
    if (!selectedType) {
      alert('Пожалуйста, выберите тип комнаты');
      return;
    }

    try {
      const response = await fetch('/rooms', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ type: selectedType }),
      });

      if (!response.ok) {
        throw new Error(response.statusText);
      }

      const newRoom = await response.json();
      setRooms(prev => [...prev, newRoom]);
      setShowForm(false);
      setSelectedType('');
    } catch (error) {
      console.error(error);
      alert('Не удалось создать комнату');
    }
  };

  return (
    <div className="table">
      <h1>Мои комнаты</h1>
      <div className="devices-list">
        {rooms.map(room => (
          <div key={room.id} className="device-card">
            <p>{room.name}</p>
            <p>Тип: {room.type}</p>
          </div>
        ))}
      </div>

      {showForm ? (
        <>
          <label htmlFor="room-type">Выберите тип комнаты:</label>
          <select
            id="room-type"
            value={selectedType}
            onChange={e => setSelectedType(e.target.value)}
          >
            <option value="">-- выберите --</option>
            {roomTypes.map(rt => (
              <option key={rt.value} value={rt.value}>
                {rt.label}
              </option>
            ))}
          </select>
          <button onClick={handleCreate}>Создать комнату</button>
        </>
      ) : (
        <button onClick={handleAddClick}>+ Новая комната</button>
      )}
    </div>
  );
}
