import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const BACKEND = process.env.REACT_APP_BACKEND_URL;
const specialChars = /[!@#*()\-]/;
const uppercaseRegex = /[A-Z]/;

export default function Join() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [currency, setCurrency] = useState('USD');
  const [price, setPrice] = useState('');
  const [error, setError] = useState('');

  const isLengthValid = password.length >= 6;
  const hasUppercase = uppercaseRegex.test(password);
  const hasSpecialChar = specialChars.test(password);
  const isPasswordValid = isLengthValid && hasUppercase && hasSpecialChar;

  const handleRegister = async () => {
    if (!isPasswordValid) {
      setError('Password does not meet requirements');
      return;
    }
    setError('');
    const body = { firstName, lastName, email, password, currencyCode: currency, pricePerWatt: parseFloat(price) };
    try {
      const res = await fetch(`${BACKEND}/auth/register`, {
        method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(body)
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Registration error');
      localStorage.setItem('token', data.accessToken);
    } catch (e) {
      setError(e.message);
    }
  };

  return (
    <div className="table">
      <h2>Join us!</h2>
      <input placeholder="First Name" value={firstName} onChange={e => setFirstName(e.target.value)} />
      <input placeholder="Last Name" value={lastName} onChange={e => setLastName(e.target.value)} />
      <input placeholder="Email" type="email" value={email} onChange={e => setEmail(e.target.value)} />
      <input placeholder="Password" type="password" value={password} onChange={e => setPassword(e.target.value)} />
      <input placeholder="Currency (e.g. USD)" value={currency} onChange={e => setCurrency(e.target.value)} />
      <input placeholder="Price per Watt" type="number" value={price} onChange={e => setPrice(e.target.value)} />
      <ul style={{ listStyle: 'none', padding: 0, margin: '1rem auto', textAlign: 'left', maxWidth: 300 }}>
        <li style={{ color: isLengthValid ? 'lightgreen' : '#ff6b6b' }}>
          {isLengthValid ? '✔' : '✖'} Min. 6 characters
        </li>
        <li style={{ color: hasUppercase ? 'lightgreen' : '#ff6b6b' }}>
          {hasUppercase ? '✔' : '✖'} One uppercase letter
        </li>
        <li style={{ color: hasSpecialChar ? 'lightgreen' : '#ff6b6b' }}>
          {hasSpecialChar ? '✔' : '✖'} One special char (!@#*()-)
        </li>
      </ul>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <Link to="/appointment">
        <button onClick={handleRegister} disabled={!firstName || !lastName || !email || !price || !isPasswordValid}>
          Proceed
        </button>
      </Link>
    </div>
  );
}
